INSERT INTO `conditions_albanian` VALUES (1,'Aneurizma e aortës abdominale','abdominal_aortic_aneurysm','https://www.nhs.uk/conditions/abdominal-aortic-aneurysm/','{\"title\": \"Aneurizma e aortës abdominale\", \"url\": \"https://www.nhs.uk/conditions/abdominal-aortic-aneurysm/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë në shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne i përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu te Shëndeti nga A në Z\", \"Një aneurizëm i aortës abdominale (AAA) është një zgjerim në aortë, arteria që bart gjakun nga zemra në bark (abdomen). Shumica e aneurizmave nuk shkaktojnë asnjë problem, por ato mund të jenë serioze sepse ekziston një rrezik që ato të shpërthejnë (të çajnë).\"]}, {\"title\": \"Simptomat e aneurizmës së aortës abdominale\", \"paragraphs\": [\"Aneurizma e aortës abdominale shpesh nuk ka simptoma.\", \"Zakonisht e zbuloni që keni një të tillë gjatë një testi të skanimit të aneurizmës së aortës abdominale ose gjatë testeve për një gjendje tjetër.\", \"Nëse një aneurizëm rritet, ndonjëherë mund të vini re:\"]}, {\"title\": \"Këshillë jo urgjente: Konsultohuni me një mjek nëse:\", \"paragraphs\": [\"Këto simptoma mund të shkaktohen nga shumë gjëra dhe nuk do të thotë që keni një aneurizëm të aortës abdominale, por është më mirë t\'i kontrolloni.\"]}, {\"title\": \"Veprim i menjëhershëm: Thirrni 999 nëse ju ose dikush tjetër:\", \"paragraphs\": [\"Këto mund të jenë shenja të shpërthimit të një aneurizme të aortës abdominale (çarje). Kjo është një emergjencë jetëkërcënuese që duhet të trajtohet në spital sa më shpejt të jetë e mundur.\"]}, {\"title\": \"Testet për aneurizën e aortës abdominale\", \"paragraphs\": [\"Testi kryesor për të zbuluar nëse keni një aneurizëm të aortës abdominale është një skanim me ultrazë të barkut tuaj.\", \"Një pajisje e vogël vendoset në barkun tuaj dhe imazhet e brendshme të barkut tuaj do të shfaqen në një ekran.\", \"Zakonisht do t\'ju thuhet nëse keni një aneurizëm në fund të testit.\", \"Gjithashtu do t\'ju thuhet se sa e madhe është:\"]}, {\"title\": \"Skanimi për aneurizën e aortës abdominale\", \"paragraphs\": [\"Një test me ultrazë ofrohet të gjithë burrave kur bëhen 65 vjeç, pasi ata janë më të rrezikuar për të patur një aneurizëm të aortës abdominale.\", \"Kjo quhet skanim për aneurizën e aortës abdominale.\"]}, {\"title\": \"Trajtimi për aneurizën e aortës abdominale\", \"paragraphs\": [\"Trajtimi për një aneurizëm të aortës abdominale zakonisht varet nga madhësia e tij dhe nëse keni simptoma.\", \"Nëse është i vogël, mund të rekomandohen ndryshime në stilin e jetës si ndalimi i pirjes së duhanit dhe ushqimi i shëndetshëm për të ndihmuar në parandalimin e rritjes së tij.\", \"Ndonjëherë mund t\'ju duhet:\", \"Kirurgjia ka rreziqe dhe efekte anësore të cilat mjeku ose kirurgu juaj duhet t\'i diskutojë me ju.\", \"Aneurizmat e aortës abdominale shpesh rriten me kalimin e kohës. Zakonisht do t\'ju duhen skanime të rregullta për të parë nëse po rritet.\", \"Nëse aneurizma juaj shpërthen, do t\'ju duhet kirurgji urgjente për ta trajtuar.\"]}, {\"title\": \"Si të ulni rrezikun e të paturit të një aneurizme të aortës abdominale\", \"paragraphs\": [\"Bërja e ndryshimeve të shëndetshme në stilin e jetës mund të ndihmojë në uljen e rrezikut të të paturit të një aneurizme të aortës abdominale, ose të ndalojë një të tillë nga rritja.\"]}, {\"title\": \"Bëni\", \"paragraphs\": [\"ushqehuni shëndetshëm\", \"bëni ushtrime fizike rregullisht\", \"përpiquni të humbni peshë nëse jeni mbipeshë\", \"përpiquni të zvogëloni konsumin e alkoolit\"]}, {\"title\": \"Mos bëni\", \"paragraphs\": [\"mos pi duhan\", \"Gjeni se si të merrni ndihmë për të ndaluar pirjen e duhanit\"]}, {\"title\": \"Shkaqet e një aneurizme të aortës abdominale\", \"paragraphs\": [\"Një aneurizëm i aortës abdominale ndodh kur arteria kryesore që bart gjakun nga zemra në bark (aorta) dobësohet.\", \"Nuk është gjithmonë e qartë se çfarë e shkakton atë, por disa njerëz kanë një shans më të lartë për ta patur.\", \"Jeni më të rrezikuar nëse:\", \"Ndonjëherë një aneurizëm i aortës abdominale mund të shkaktohet nga një infeksion, por kjo është e rrallë.\"]}, {\"title\": \"Video: Aneurizma e aortës abdominale\", \"paragraphs\": [\"Kjo video tregon se si duket një aneurizëm i aortës abdominale.\", \"Kjo është një dritare modale.\", \"Fillimi i dritares së dialogut. Escape do ta anulojë dhe mbyllë dritaren.\", \"Fundi i dritares së dialogut.\", \"Kjo është një dritare modale. Kjo modale mund të mbyllet duke shtypur tastin Escape ose duke aktivizuar butonin e mbylljes.\", \"Kjo është një dritare modale. Kjo modale mund të mbyllet duke shtypur tastin Escape ose duke aktivizuar butonin e mbylljes.\", \"Faqja e fundit e rishikuar: 14 qershor 2023Rishikimi i ardhshëm: 14 qershor 2026\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e mbretërisë\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/ultrasound-scan/\", \"https://www.nhs.uk/conditions/high-blood-pressure-hypertension/\", \"https://www.nhs.uk/conditions/high-cholesterol/\", \"https://www.nhs.uk/conditions/atherosclerosis/\", \"https://www.nhs.uk/conditions/chronic-obstructive-pulmonary-disease-copd/\", \"https://www.nhs.uk/conditions/marfan-syndrome/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(2,'Ekzaminimi për anevrizmën e aortës abdominale (AAA)','abdominal_aortic_aneurysm_aaa_screening','https://www.nhs.uk/conditions/abdominal-aortic-aneurysm-screening/','{\"title\": \"Ekzaminimi për anevrizmën e aortës abdominale (AAA)\", \"url\": \"https://www.nhs.uk/conditions/abdominal-aortic-aneurysm-screening/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë në shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu te Shëndeti nga A në Z\", \"Ekzaminimi për anevrizmën e aortës abdominale (AAA) është një test për të kontrolluar nëse ka një fryrje në aortën tuaj, e cila është enja kryesore e gjakut që bart gjakun nga zemra në bark (abdomen).\", \"Ekzaminimi AAA ofrohet për të gjithë burrat mbi moshën 64 vjeç. Çdo person i caktuar si mashkull në lindje që është mbi moshën 64 vjeç mund ta ketë atë.\"]}, {\"title\": \"Pse bëhet ekzaminimi për anevrizmën e aortës abdominale (AAA)\", \"paragraphs\": [\"Ekzaminimi për anevrizmën e aortës abdominale (AAA) bëhet për të kontrolluar nëse ka ndonjë fryrje (anevrizma) në enën kryesore të gjakut në barkun tuaj (abdomen), e quajtur aortë. Aorta shkon nga zemra juaj deri në bark.\", \"Ekipi Multimedia - nhs.uk\", \"Ekzaminimi AAA mund të ndihmojë në gjetjen e një anevrizme të aortës abdominale herët. Kjo do të thotë që ajo mund të monitorohet dhe trajtohet, nëse është e nevojshme, në mënyrë që të jetë më pak e mundshme të plasaritet (shpërthejë), gjë që mund të jetë jetëkërcënuese.\"]}, {\"title\": \"Për kë është ekzaminimi për anevrizmën e aortës abdominale (AAA)\", \"paragraphs\": [\"Të gjithë burrat që jetojnë në Angli ftohen për ekzaminimin e anevrizmës së aortës abdominale (AAA) vitin kur mbushin 65 vjeç.\", \"Ekzaminimi AAA rekomandohet për çdo person të caktuar si mashkull në lindje. Kjo përfshin burrat, gratë trans dhe personat jo-binare.\", \"Do ta keni testin e ekzaminimit AAA një herë, përveç nëse gjendet një anevrizmë.\", \"Burrat ftohen për ekzaminimin AAA sepse ata kanë më shumë gjasa të kenë një anevrizmë të aortës abdominale.\", \"Ekzaminimi AAA zakonisht nuk ofrohet për njerëzit me rrezik më të ulët për të patur një anevrizmë të aortës abdominale, duke përfshirë:\", \"Nëse jeni një burrë trans, grua trans ose jeni jo-binar, nevoja juaj për ekzaminimin AAA varet nga gjinia që ju është caktuar në lindje:\", \"Nëse jeni të regjistruar si mashkull me një mjek të përgjithshëm (GP), do të ftoheni automatikisht për ekzaminimin AAA vitin kur mbushni 65 vjeç.\", \"Nëse jeni të regjistruar si femër me një mjek të përgjithshëm (GP), mund të keni nevojë të bisedoni me klinikën e mjekut të përgjithshëm ose të telefononi shërbimin lokal të ekzaminimit AAA për të kërkuar një takim.\"]}, {\"title\": \"Nëse keni një rrezik më të lartë për anevrizmën e aortës abdominale\", \"paragraphs\": [\"Nëse nuk jeni të përshtatshëm për ekzaminimin AAA por mendoni se jeni në rrezik më të lartë për të patur një anevrizmë të aortës abdominale, bisedoni me një mjek të përgjithshëm për mundësinë e marrjes së një skanimi.\", \"Ju mund të jeni në rrezik më të lartë nëse:\"]}, {\"title\": \"Si të merrni ekzaminimin për anevrizmën e aortës abdominale (AAA)\", \"paragraphs\": [\"Gjatë vitit kur mbushni 65 vjeç, do të merrni një letër që ju fton për një takim të paracaktuar të ekzaminimit të anevrizmës së aortës abdominale (AAA).\", \"Letra zakonisht dërgohet 3 deri në 6 javë para takimit.\", \"Ekzaminimi AAA zakonisht zhvillohet në klinikat e mjekëve të përgjithshëm ose qendrat shëndetësore, dhe ndonjëherë në spitale.\", \"Nëse keni nevojë të ndryshoni takimin tuaj ose nuk keni marrë një letër ftese dhe mendoni se duhet të kishit, kontaktoni shërbimin lokal të ekzaminimit AAA.\", \"Është zgjedhja juaj nëse dëshironi të bëni ekzaminimin AAA. Shumica e anevrizmave të aortës abdominale nuk kanë ndonjë simptomë, kështu që ekzaminimi është mënyra më e mirë për të gjetur një të tillë.\", \"Gjetja e një anevrizme të aortës abdominale zvogëlon mundësinë që ajo të plasaritet (shpërthejë), gjë që mund të jetë jetëkërcënuese.\", \"Nëse nuk dëshironi të bëni ekzaminimin për AAA, telefononi shërbimin lokal të ekzaminimit AAA.\", \"Mund të telefononi përsëri për të kërkuar ekzaminimin nëse vendosni që dëshironi ta bëni atë në të ardhmen.\"]}, {\"title\": \"Si bëhet ekzaminimi për anevrizmën e aortës abdominale (AAA)\", \"paragraphs\": [\"Testi kryesor për të zbuluar nëse keni një anevrizmë të aortës abdominale (AAA) është një skanim me ultrazë të barkut tuaj. Zakonisht zgjat rreth 10 deri në 15 minuta dhe është i padhimbshëm.\", \"Një pajisje e vogël e mbajtur në dorë e quajtur sondë vendoset në barkun tuaj dhe imazhet e brendshme të barkut tuaj do të shfaqen në një ekran.\", \"Profesionisti mjekësor që kryen testin do të kontrollojë për ndonjë fryrje ose ënjtje të aortës.\", \"Ndonjëherë mund të mos jetë e mundur të merret një pamje e qartë e aortës tuaj. Nëse ndodh kjo, mund të keni nevojë të bëni një skanim tjetër.\"]}, {\"title\": \"Marrja e rezultateve të ekzaminimit të anevrizmës së aortës abdominale (AAA)\", \"paragraphs\": [\"Do të merrni rezultatet e ekzaminimit të anevrizmës së aortës abdominale (AAA) në fund të skanimit. Rezultatet do t\'i dërgohen klinikës suaj të mjekut të përgjithshëm.\"]}, {\"title\": \"Nëse nuk gjendet një anevrizmë\", \"paragraphs\": [\"Nëse nuk keni një anevrizmë, nuk keni nevojë të bëni asgjë dhe nuk do të keni nevojë për më shumë skanime.\"]}, {\"title\": \"Nëse gjendet një anevrizmë\", \"paragraphs\": [\"Nëse gjendet një anevrizmë gjatë skanimit tuaj, çfarë ndodh më pas varet nga madhësia e anevrizmës.\", \"E vogël (3cm deri në 4.4cm)\", \"Do të bëni një skanim çdo vit për të parë nëse anevrizma është bërë më e madhe dhe do të takoni një infermiere specialiste për këshilla\", \"E mesme (4.5cm deri në 5.4cm)\", \"Do të bëni një skanim çdo 3 muaj për të parë nëse anevrizma është bërë më e madhe dhe do të takoni një infermiere specialiste për këshilla\", \"E madhe (5.5cm ose më e madhe)\", \"Do të referoheni te një specialist i quajtur kirurg vaskular për më shumë teste dhe trajtim të mundshëm\"]}, {\"title\": \"Monitorimi i një anevrizme të vogël ose të mesme\", \"paragraphs\": [\"Nëse keni një anevrizmë të vogël ose të mesme, do t\'ju ofrohet një takim për të takuar një infermiere specialiste.\", \"Ata do t\'u përgjigjen çdo pyetjeje që mund të keni dhe do të bisedojnë me ju për:\"]}, {\"title\": \"Më shumë informacion rreth ekzaminimit të anevrizmës së aortës abdominale (AAA)\", \"paragraphs\": [\"Faqja e fundit e rishikuar: 11 Nëntor 2024Rishikimi i ardhshëm: 11 Nëntor 2027\"]}, {\"title\": \"Lidhje mbështetëse\", \"paragraphs\": [\"© E drejta e autorit e Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/social-care-and-support-guide/\", \"https://www.nhs.uk/conditions/abdominal-aortic-aneurysm/\", \"https://www.nhs.uk/conditions/ultrasound-scan/\", \"https://www.nhs.uk/conditions/social-care-and-support-guide/\", \"https://www.nhs.uk/conditions/coronavirus-covid-19/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(3,'Aborti','abortion','https://www.nhs.uk/conditions/abortion/','{\"title\": \"Aborti\", \"url\": \"https://www.nhs.uk/conditions/abortion/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tek Shëndeti nga A në Z\", \"Zbuloni çfarë është një abort, si të përgatiteni për të dhe çfarë përfshin.\"]}, {\"title\": \"Lidhje mbështetëse\", \"paragraphs\": [\"© E drejta e autorit e mbretërisë\"]}], \"sublinks\": null, \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(4,'Akanthosis nigricans','acanthosis_nigricans','https://www.nhs.uk/conditions/acanthosis-nigricans/','{\"title\": \"Akanthosis nigricans\", \"url\": \"https://www.nhs.uk/conditions/acanthosis-nigricans/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne i përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tek Shëndeti nga A në Z\", \"Akanthosis nigricans është emri për njollat e thata dhe të errëta të lëkurës që zakonisht shfaqen në sqetull, qafë ose ingjinal. Mund të jetë një shenjë e një gjendjeje themelore, kështu që duhet të kontrollohet nga një mjek i përgjithshëm.\"]}, {\"title\": \"Kontrolloni nëse keni akanthosis nigricans\", \"paragraphs\": [\"Simptoma kryesore e akanthosis nigricans janë njollat e lëkurës që janë më të errëta dhe më të trasha se zakonisht.\", \"Ato mund të shfaqen kudo në trup.\", \"Benedicte Desrus / Alamy Stock Photo https://www.alamy.com/stock-photo-childhood-obesity-in-mexico-37878235.html?pv=1&stamp=2&imageid=D82F3D1E-88DD-48FC-8591-AE73FADBC974&p=68490&n=0&orientation=0&pn=1&searchtype=0&IsFromSearch=1&srch=foo%3dbar%26st%3d0%26pn%3d1%26ps%3d100%26sortby%3d2%26resultview%3dsortbyPopular%26npgs%3d0%26qt%3dC5HE23%26qt_raw%3dC5HE23%26lic%3d3%26mr%3d0%26pr%3d0%26ot%3d0%26creative%3d%26ag%3d0%26hc%3d0%26pc%3d%26blackwhite%3d%26cutout%3d%26tbar%3d1%26et%3d0x000000000000000000000%26vp%3d0%26loc%3d0%26imgt%3d0%26dtfr%3d%26dtto%3d%26size%3d0xFF%26archive%3d1%26groupid%3d%26pseudoid%3d%26a%3d%26cdid%3d%26cdsrt%3d%26name%3d%26qn%3d%26apalib%3d%26apalic%3d%26lightbox%3d%26gname%3d%26gtype%3d%26xstx%3d0%26simid%3d%26saveQry%3d%26editorial%3d1%26nu%3d%26t%3d%26edoptin%3d%26customgeoip%3d%26cap%3d1%26cbstore%3d1%26vd%3d0%26lb%3d%26fi%3d2%26edrf%3d0%26ispremium%3d1%26flip%3d0%26pl%3d\", \"CID, ISM /SCIENCE PHOTO LIBRARY https://www.sciencephoto.com/media/459432/view\", \"ANDRE LABBE, ISM/SCIENCE PHOTO LIBRARY https://www.sciencephoto.com/media/886025/view\", \"Njollat shpesh shfaqen gradualisht pa ndonjë simptomë tjetër.\", \"Ndonjëherë lëkura mund të jetë kruajtëse.\"]}, {\"title\": \"Çfarë ndodh në takimin tuaj\", \"paragraphs\": [\"Një mjek i përgjithshëm zakonisht mund të tregojë nëse është akanthosis nigricans duke parë lëkurën tuaj.\", \"Edhe pse akanthosis nigricans zakonisht është i padëmshëm, është më mirë të kontrolloni çdo ndryshim të lëkurës.\", \"Rrallë, mund të jetë një shenjë e diçkaje më serioze, si p.sh. kancer.\", \"Mund të keni nevojë për disa teste për të gjetur se çfarë shkakton njollat.\"]}, {\"title\": \"Shkaqet e akanthosis nigricans\", \"paragraphs\": [\"Shkaku më i zakonshëm i akanthosis nigricans është obeziteti.\", \"Shkaqe të tjera përfshijnë:\", \"Ndonjëherë njerëz të shëndetshëm pa ndonjë gjendje tjetër marrin akanthosis nigricans. Kjo është më e zakonshme tek njerëzit me lëkurë të zezë ose kafe.\"]}, {\"title\": \"Trajtimi i akanthosis nigricans\", \"paragraphs\": [\"Pasi një mjek i përgjithshëm e di se çfarë shkakton akanthosis nigricans, ai mund të rekomandojë trajtimin më të mirë.\", \"Njollat duhet të zbehen me kalimin e kohës pasi të trajtohet shkaku.\", \"Nëse jeni shumë mbipeshë, një mjek i përgjithshëm mund të rekomandojë humbje peshe.\", \"Në varësi të shkakut, ata mund të rekomandojnë gjithashtu:\", \"Nuk ka trajtim specifik për vetë njollat. Një specialist i lëkurës (dermatolog) mund të jetë në gjendje të sugjerojë trajtime për të përmirësuar pamjen e tyre, por gjetja dhe trajtimi i shkakut zakonisht rekomandohet së pari.\", \"Faqja e fundit e rishikuar: 04 Korrik 2024Rishikimi i ardhshëm: 04 Korrik 2027\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/obesity/\", \"https://www.nhs.uk/conditions/type-2-diabetes/\", \"https://www.nhs.uk/conditions/cushings-syndrome/\", \"https://www.nhs.uk/conditions/underactive-thyroid-hypothyroidism/\", \"https://www.nhs.uk/conditions/stomach-cancer/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(5,'Qasja në fizioterapi\n    \n - \n      Fizioterapia','accessing_physiotherapy_physiotherapy','https://www.nhs.uk/conditions/physiotherapy/accessing/','{\"title\": \"Qasja në fizioterapi\\n    \\n - \\n      Fizioterapia\", \"url\": \"https://www.nhs.uk/conditions/physiotherapy/accessing/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu teFizioterapia\"]}, {\"title\": \"Përmbajtja\", \"paragraphs\": [\"Nëse keni nevojë për fizioterapi, një numër opsionesh të ndryshme janë në dispozicion për ju.\", \"Ju mund të shihni një fizioterapist:\", \"Fizioterapia gjithashtu ndonjëherë mund të arrihet përmes shërbimeve të shëndetit në punë, organizatave bamirësie, grupeve të pacientëve dhe sektorit vullnetar.\"]}, {\"title\": \"Fizioterapia në NHS\", \"paragraphs\": [\"Fizioterapia është e disponueshme pa pagesë në NHS në të gjithë Mbretërinë e Bashkuar.\", \"Në varësi të vendit ku jetoni, mund të jeni në gjendje të referoni veten ose mund të keni nevojë të vizitoni mjekun tuaj të përgjithshëm ose konsulentin fillimisht. Pas diskutimit të simptomave tuaja me mjekun tuaj, ata mund t\'ju referojnë më pas tek një fizioterapist.\", \"Ju mund të kërkoni për shërbime fizioterapie afër jush për të zbuluar se ku mund të referoheni.\"]}, {\"title\": \"Vetëreferimi për fizioterapi në NHS\", \"paragraphs\": [\"Disa zona në Mbretërinë e Bashkuar ofrojnë një shërbim vetëreferimi, që do të thotë se mund të bëni një takim për të parë një fizioterapist të NHS pa pasur nevojë të shihni fillimisht një mjek.\", \"Megjithatë, kjo nuk është e disponueshme kudo. Stafi në klinikën tuaj të mjekut të përgjithshëm ose në spital duhet të jetë në gjendje t\'ju tregojë nëse është e disponueshme në zonën tuaj.\", \"Vetëreferimi është veçanërisht i përshtatshëm për njerëzit me kushte relativisht të thjeshta si dhimbje në nyje, sforcime ose lëndime të tjera.\", \"Nëse keni nevoja më komplekse – për shembull, keni probleme lëvizjeje të shkaktuara nga një goditje në tru ose sklerozë e shumëfishtë (MS) – mund të keni ende nevojë për një referim nga një mjek.\"]}, {\"title\": \"Qasje e drejtpërdrejtë në fizioterapi\", \"paragraphs\": [\"Disa fizioterapistë gjithashtu punojnë në praktikat e mjekëve të përgjithshëm si pika e parë e kontaktit për pacientët me probleme muskuloskeletale, si dhimbje në qafë ose shpinë, dhe ata me kushte afatgjata, si MS ose goditje në tru.\", \"Kur kontaktoni klinikën tuaj të mjekut të përgjithshëm, mund t\'ju ofrohet të shihni fizioterapistin direkt, në vend që të duhet të shihni fillimisht mjekun e përgjithshëm.\", \"Fizioterapistët që punojnë në këto role mund të kenë aftësi të avancuara, si përshkrimi i ilaçeve dhe porositja e skanimeve.\"]}, {\"title\": \"Fizioterapi private\", \"paragraphs\": [\"Listat e pritjes për fizioterapi në NHS mund të jenë të gjata dhe disa njerëz zgjedhin të kenë trajtim privat në vend të kësaj. Nëse shihni një fizioterapist privat, do të duhet të paguani për trajtimin.\", \"Zakonisht mund t\'i drejtoheni një fizioterapisti privat direkt pa një referim nga një mjek.\", \"Kur zgjidhni një fizioterapist privat, sigurohuni që ata janë:\", \"Ju mund të gjeni një fizioterapist privat të certifikuar afër jush duke përdorur drejtorinë Physio2u të CSP. Ju gjithashtu mund të përdorni funksionin e kërkimit Find a physio në faqen e internetit të Physio First.\"]}, {\"title\": \"Shërbimet e shëndetit në punë\", \"paragraphs\": [\"Fizioterapia mund të jetë e disponueshme përmes vendit tuaj të punës.\", \"Disa kompani ofrojnë shërbime të shëndetit në punë, të cilat përfshijnë trajtim fizioterapik. Kontrolloni me menaxherin tuaj ose departamentin e Burimeve Njerëzore për të parë nëse është e disponueshme aty ku punoni.\", \"Faqja e fundit e rishikuar: 09 Dhjetor 2022Rishikimi i ardhshëm: 09 Dhjetor 2025\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e Krunit\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/stroke/\", \"https://www.nhs.uk/conditions/multiple-sclerosis/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(6,'Achalasia','achalasia','https://www.nhs.uk/conditions/achalasia/','{\"title\": \"Achalasia\", \"url\": \"https://www.nhs.uk/conditions/achalasia/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë në shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne i përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu te Shëndeti nga A në Z\", \"Achalasia është një çrregullim i rrallë i tubit të ushqimit (ezofagut), i cili mund ta bëjë të vështirë të gëlltiten ushqimi dhe pijet.\", \"Normalisht, muskujt e ezofagut tkurren për të shtrydhur ushqimin drejt stomakut. Një unazë muskulore në fund të tubit të ushqimit pastaj relaksohet për të lejuar ushqimin të hyjë në stomak.\", \"Në achalasia, muskujt në ezofag nuk tkurren si duhet dhe unaza muskulore mund të mos hapet si duhet, ose nuk hapet fare. Ushqimi dhe pijet nuk mund të kalojnë në stomak dhe mbeten të mbërthyer. Shpesh kthehen prapa.\"]}, {\"title\": \"Simptomat e achalasias\", \"paragraphs\": [\"Jo të gjithë me achalasia do të kenë simptoma.\", \"Por shumica e njerëzve me achalasia do ta gjejnë të vështirë të gëlltisin ushqim ose pije (e njohur si disfagia). Gëlltitja tenton të bëhet gradualisht më e vështirë ose e dhimbshme gjatë disa vitesh, deri në pikën ku ndonjëherë është e pamundur.\", \"Simptomat e tjera përfshijnë:\", \"Simptomat e achalasias mund të fillojnë në çdo kohë të jetës.\", \"Problemet e gëlltitjes mund të shkaktohen gjithashtu nga kanceret e gojës, fytit dhe ezofagut. Ekziston një lidhje midis achalasias afatgjate dhe rrezikut të zhvillimit të kancerit të ezofagut, por rreziku është i vogël.\", \"Është gjithmonë e rëndësishme të kontrolloni simptomat menjëherë, edhe nëse simptomat tuaja nuk ju shqetësojnë.\"]}, {\"title\": \"Shkaqet e achalasias\", \"paragraphs\": [\"Achalasia besohet se ndodh kur nervat në ezofag dëmtohen dhe pushojnë së punuari si duhet, kjo është arsyeja pse muskujt dhe unaza muskulore nuk funksionojnë. Shkaku i saktë i kësaj është i panjohur.\", \"Tek disa njerëz, mund të jetë e lidhur me një infeksion viral. Mund të jetë gjithashtu e lidhur me një gjendje autoimune, ku sistemi imunitar i trupit sulmon qelizat, indet dhe organet e shëndetshme.\", \"Në raste të rralla, është e mundur që achalasia të shkaktohet nga një gjen i gabuar.\"]}, {\"title\": \"Diagnostikimi i achalasias\", \"paragraphs\": [\"Nëse një mjek i përgjithshëm mendon se keni achalasia, do të referoheni në spital për të bërë disa teste diagnostikuese. Achalasia mund të diagnostikohet gjithashtu gjatë një hetimi, si një röntgen i gjoksit, për një arsye tjetër.\", \"Testet për achalasia përfshijnë:\"]}, {\"title\": \"Trajtimet për achalasia\", \"paragraphs\": [\"Nuk ka kurë për achalasia, por trajtimi mund të ndihmojë në lehtësimin e simptomave dhe të bëjë gëlltitjen më të lehtë.\", \"Mjekët tuaj do të flasin me ju për rreziqet dhe përfitimet e opsioneve të ndryshme të trajtimit.\"]}, {\"title\": \"Medikamentet\", \"paragraphs\": [\"Medikamentet, si nitratet ose nifedipina, mund të ndihmojnë në relaksimin e muskujve në ezofagun tuaj. Kjo e bën gëlltitjen më të lehtë dhe më pak të dhimbshme për disa njerëz, megjithëse ato nuk funksionojnë për të gjithë.\", \"Efekti zgjat vetëm për një kohë të shkurtër, kështu që medikamentet mund të përdoren për të lehtësuar simptomat ndërsa prisni për një trajtim më të përhershëm. Ato mund të shkaktojnë dhimbje koke, por kjo zakonisht përmirësohet me kalimin e kohës.\"]}, {\"title\": \"Zgjerimi i muskulit (dilatimi me balonë)\", \"paragraphs\": [\"Nën një sedativ, ose ndonjëherë një anestezi të përgjithshme, një balonë futet në ezofag duke përdorur një tub të gjatë, të hollë dhe fleksibël (endoskop). Balona pastaj fryhet për të ndihmuar në zgjerimin e unazës muskulore që lejon ushqimin të hyjë në stomakun tuaj.\", \"Kjo përmirëson gëlltitjen për shumicën e njerëzve, por mund t\'ju duhet trajtim disa herë para se simptomat tuaja të përmirësohen.\", \"Dilatimi me balonë mbart një rrezik të vogël të grisjes së ezofagut (grishje ezofagjale), e cila mund të kërkojë operacion emergjent.\"]}, {\"title\": \"Injektimi i Botoksit\", \"paragraphs\": [\"Duke përdorur një endoskop, botoksi injektohet në unazën muskulore që lejon ushqimin të hyjë në stomakun tuaj, duke e bërë atë të relaksohet.\", \"Zakonisht është efektiv për disa muaj dhe ndonjëherë për disa vjet, por duhet të përsëritet. Kjo zakonisht është e padhimbshme dhe mund të përdoret për lehtësim të përkohshëm tek njerëzit që nuk mund të kenë trajtime të tjera.\"]}, {\"title\": \"Operacioni\", \"paragraphs\": [\"Nën anestezi të përgjithshme, fijet muskulore në unazën muskulore që lejon ushqimin të hyjë në stomakun tuaj priten. Kjo bëhet duke përdorur kirurgjinë me vrima të vogla (laparoskopi) dhe quhet miotomi i Hellerit.\", \"Kjo mund të bëjë gëlltitjen më të lehtë përgjithmonë.\", \"Shpesh një procedurë e dytë do të bëhet në të njëjtën kohë për të ndaluar refluxin acid dhe djegien e zemrës, të cilat mund të jenë një efekt anësor i operacionit të miotomisë së Hellerit. Kirurgu juaj do të flasë me ju për këtë dhe për çdo opsion tjetër kirurgjik që mund të jetë opsioni më i mirë për ju.\"]}, {\"title\": \"Trajtimi pas operacionit\", \"paragraphs\": [\"Dilatimi me balonë dhe operacioni mund të shkaktojnë efekte anësore si refluxi acid dhe djegia e zemrës dhe dhimbje gjoksi. Një mjek i përgjithshëm mund të jetë në gjendje të preskruajë medikamente për të ndihmuar me këtë, dhe kirurgu juaj mund të sugjerojë që ju ta merrni këtë medikament rutinisht.\", \"Është normale që dhimbja e gjoksit të zgjasë për një kohë pas trajtimit.\", \"Ju duhet të shihni një mjek të përgjithshëm nëse ende keni vështirësi në gëllitje ose po vazhdoni të humbni peshë pas trajtimit.\", \"Lexoni rreth jetesës me achalasia në faqen e internetit të Achalasia Action\", \"Faqja e fundit e rishikuar: 05 Dhjetor 2023Rishikimi i ardhshëm: 05 Dhjetor 2026\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e mbretërisë\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/swallowing-problems-dysphagia/\", \"https://www.nhs.uk/conditions/heartburn-and-acid-reflux/\", \"https://www.nhs.uk/conditions/chest-pain/\", \"https://www.nhs.uk/conditions/chest-infection/\", \"https://www.nhs.uk/conditions/unintentional-weight-loss/\", \"https://www.nhs.uk/conditions/endoscopy/\", \"https://www.nhs.uk/conditions/endoscopy/\", \"https://www.nhs.uk/conditions/laparoscopy/\", \"https://www.nhs.uk/conditions/heartburn-and-acid-reflux/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(7,'Vendim i paraprak për të refuzuar trajtimin (testament i gjallë)','advance_decision_to_refuse_treatment_living_will_','https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/advance-decision-to-refuse-treatment/','{\"title\": \"Vendim i paraprak për të refuzuar trajtimin (testament i gjallë)\", \"url\": \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/advance-decision-to-refuse-treatment/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë në shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne i përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tePlanifikimi paraprak për kujdesin në fund të jetës\", \"Një vendim i paraprak për të refuzuar trajtimin i bën të ditur ekipit tuaj të kujdesit shëndetësor dëshirat tuaja nëse nuk jeni në gjendje t\'i komunikoni ato.\"]}, {\"title\": \"Çfarë është një vendim i paraprak?\", \"paragraphs\": [\"Një vendim i paraprak (ndonjëherë i njohur si një vendim i paraprak për të refuzuar trajtimin, një ADRT, ose një testament i gjallë) është një vendim që mund të merrni tani për të refuzuar një lloj specifik trajtimi në një kohë në të ardhmen.\", \"Ai i bën të ditur familjes, kujdestarëve dhe profesionistëve të shëndetit dëshirat tuaja për të refuzuar trajtimin nëse nuk jeni në gjendje të merrni ose të komunikoni ato vendime vetë.\", \"Trajtimet që vendosni të refuzoni duhet të jenë të emërtuara në vendimin e paraprak.\", \"Mund të dëshironi të refuzoni një trajtim në disa situata, por jo në të tjera. Nëse është kështu, duhet të jeni të qartë për të gjitha rrethanat në të cilat dëshironi të refuzoni këtë trajtim.\", \"Një vendim i paraprak nuk është i njëjtë me një deklaratë të paraprak.\", \"Lexoni rreth deklaratave të paraprak\", \"Vendimi për të refuzuar një trajtim nuk është i njëjtë me të kërkuar dikë të përfundojë jetën tuaj ose t\'ju ndihmojë ta përfundoni jetën tuaj. Eutanazia dhe vetëvrasja e asistuar janë të paligjshme në MB.\"]}, {\"title\": \"Trajtimi që mbron jetën\", \"paragraphs\": [\"Ju mund të refuzoni një trajtim që mund t\'ju mbajë gjallë, i njohur si trajtim që mbron jetën.\", \"Ky është trajtimi që zëvendëson ose mbështet funksionet e dobëta të trupit, siç janë:\", \"Mund të dëshironi ta diskutoni këtë me një mjek ose infermiere që di rreth historikut tuaj mjekësor para se të vendosni.\"]}, {\"title\": \"Kush merr një vendim të paraprak?\", \"paragraphs\": [\"Ju merrni vendimin e paraprak, për sa kohë që keni kapacitetin mendor për të marrë vendime të tilla.\", \"Mund të dëshironi të merrni një vendim të paraprak me mbështetjen e një klinikanti.\", \"Nëse vendosni të refuzoni trajtimin që mbron jetën në të ardhmen, vendimi juaj i paraprak duhet të jetë:\", \"Nëse dëshironi të refuzoni trajtime që mbështesin jetën në rrethana ku mund të vdisni si rezultat, duhet ta shprehni këtë qartë në vendimin tuaj të paraprak. Trajtimi që mbron jetën nganjëherë quhet trajtim shpëtues të jetës.\", \"Mund të jetë e dobishme të flisni me një mjek ose infermiere për llojet e trajtimeve që mund t\'ju ofrohen në të ardhmen, dhe çfarë mund të nënkuptojë nëse zgjidhni të mos i keni ato.\", \"Organizata bamirëseCompassion in Dying ka një formular vendimi të paraprakqë mund ta plotësoni në internet ose me dorë, me sugjerime për gjëra për të menduar.\"]}, {\"title\": \"A është një vendim i paraprak i detyrueshëm ligjërisht?\", \"paragraphs\": [\"Një vendim i paraprak është i detyrueshëm ligjërisht për sa kohë që:\", \"Nëse vendimi juaj i paraprak është i detyrueshëm, ai ka përparësi ndaj vendimeve të marra në interesin tuaj më të mirë nga njerëz të tjerë.\", \"Një vendim i paraprak mund të konsiderohet i vlefshëm vetëm nëse:\"]}, {\"title\": \"Si ndihmon një vendim i paraprak?\", \"paragraphs\": [\"Për sa kohë që është i vlefshëm dhe zbatohet për situatën tuaj, një vendim i paraprak i jep ekipit tuaj të kujdesit shëndetësor dhe social udhëzime klinike dhe ligjore rreth zgjedhjeve tuaja të trajtimit.\", \"Një vendim i paraprak do të përdoret vetëm nëse, në një kohë në të ardhmen, nuk jeni në gjendje të merrni vendimet tuaja për trajtimin tuaj.\"]}, {\"title\": \"A duhet të nënshkruhet dhe të dëshmohet një vendim i paraprak?\", \"paragraphs\": [\"Po, nëse zgjidhni të refuzoni trajtimin që mbron jetën.\", \"Në këtë rast, vendimi i paraprak duhet të shkruhet, dhe ju dhe një dëshmitar duhet ta nënshkruani.\", \"Ju gjithashtu duhet të përfshini një deklaratë që vendimi i paraprak zbatohet edhe nëse jeta juaj është në rrezik.\"]}, {\"title\": \"Kush duhet ta shohë atë?\", \"paragraphs\": [\"Ju keni fjalën e fundit se kush e sheh atë, por duhet të siguroheni që familja juaj, kujdestarët ose profesionistët e kujdesit shëndetësor dhe social të dinë për vendimin. Ju gjithashtu duhet t\'i informoni ata se ku ta gjejnë atë.\", \"Familja ose kujdestarët tuaj mund të duhet ta gjejnë shpejt nëse keni nevojë për trajtim emergjent dhe ata duhet t\'i tregojnë profesionistëve të shëndetit dëshirat tuaja.\", \"Ju mund të mbani një kopje në dosjet tuaja mjekësore.\"]}, {\"title\": \"CPR dhe një vendim \\\"mos provoni CPR\\\"\", \"paragraphs\": [\"CPR (rikthimi kardiopulmonar) është një trajtim që përpiqet të fillojë frymëmarrjen dhe qarkullimin e gjakut tek njerëzit:\", \"CPR mund të përfshijë:\", \"Në spital, rreth 1 nga 4 persona mbijetojnë dhe largohen nga spitali pasi kanë pasur CPR. Shkalla e mbijetesës zakonisht është më e ulët në ambiente të tjera.\", \"Shanset që CPR të funksionojë për ju mund të jenë më të larta ose më të ulëta se kjo. Për disa njerëz, nuk do të ketë asnjë shans për të përfituar nga CPR.\", \"Varet nga arsyeja pse zemra dhe frymëmarrja juaj janë ndalur, çdo sëmundje ose probleme mjekësore që keni, dhe shëndetin tuaj të përgjithshëm.\", \"Ekipi juaj i kujdesit shëndetësor mund të diskutojë me ju shanset e mundshme që CPR të funksionojë për ju.\", \"Edhe kur CPR është e suksesshme, një person mund të zhvillojë komplikime serioze, si:\", \"Njerëzit që mbijetojnë pasi kanë pasur CPR mund të kenë nevojë për mbështetje mjekësore me intensitet të lartë pas kësaj. Një numër i vogël kanë nevojë për trajtim të zgjatur në një njësi intensive (ICU).\"]}, {\"title\": \"Refuzimi i CPR paraprakisht\", \"paragraphs\": [\"Çdokush ka të drejtën të refuzojë CPR nëse dëshiron. Ju mund t\'i bëni të qartë ekipit tuaj mjekësor që nuk dëshironi të keni CPR nëse ndaloni të merrni frymë ose zemra juaj ndalon të rrahë.\", \"Kjo njihet si një vendim për të mos provuar rikthimin kardiopulmonar (DNACPR), ose urdhër DNACPR.\", \"Pasi të merret një vendim DNACPR, ai vendoset në dosjet tuaja mjekësore, zakonisht në një formular që profesionistët e shëndetit do ta njohin.\", \"Gjithashtu është e dobishme t\'i informoni familjen ose kujdestarët e tjerë për vendimin tuaj DNACPR në mënyrë që të mos jetë një surprizë për ta nëse situata lind.\", \"Nëse keni një sëmundje serioze ose po i nënshtroheni një operacioni që mund të shkaktojë ndalim të frymëmarrjes ose të zemrës, një anëtar i ekipit tuaj mjekësor duhet t\'ju pyesë për dëshirat tuaja në lidhje me CPR nëse nuk i keni bërë të ditur dëshirat tuaja më parë.\", \"Nëse nuk keni kapacitetin për të vendosur për CPR kur duhet të merret një vendim dhe nuk keni marrë një vendim të paraprak për të refuzuar trajtimin, ekipi i kujdesit shëndetësor mund të konsultohet me të afërmit tuaj për atë që ata dinë për dëshirat tuaja për të marrë një vendim në interesin tuaj më të mirë.\", \"Faqja e internetit e Këshillit të Rikthimit ka një seksion të dobishëm për Pyetjet e Bëra Shpesh: Vendimmarrja (CPR)\"]}, {\"title\": \"Më shumë nëPlanifikimi paraprak për kujdesin në fund të jetës\", \"paragraphs\": [\"Faqja e fundit e rishikuar: 19 Shtator 2023Rishikimi i ardhshëm i duhur: 19 Shtator 2026\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit të Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/end-of-life-care/\", \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/\", \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/\", \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/advance-statement/\", \"https://www.nhs.uk/conditions/euthanasia-and-assisted-suicide/\", \"https://www.nhs.uk/conditions/first-aid/cpr/\", \"https://www.nhs.uk/conditions/social-care-and-support-guide/making-decisions-for-someone-else/mental-capacity-act/\", \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(8,'Deklaratë e parapërë për dëshirat tuaja për kujdes','advance_statement_about_your_care_wishes','https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/advance-statement/','{\"title\": \"Deklaratë e parapërë për dëshirat tuaja për kujdes\", \"url\": \"https://www.nhs.uk/conditions/end-of-life-care/planning-ahead/advance-statement/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexonimëte më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tePlanifikimi përpara për kujdesin në fund të jetës\", \"Një deklaratë e parapërë është një deklaratë e shkruar që përcakton preferencat, dëshirat, besimet dhe vlerat tuaja në lidhje me kujdesin tuaj të ardhshëm.\", \"Qëllimi është të ofrojë një udhëzues për këdo që mund të duhet të marrë vendime në interesin tuaj më të mirë nëse keni humbur aftësinë për të marrë ose komunikuar vendime.\"]}, {\"title\": \"Çfarë mbulon një deklaratë e parapërë?\", \"paragraphs\": [\"Një deklaratë e parapërë mund të mbulojë çdo aspekt të shëndetit tuaj të ardhshëm ose kujdesit social. Kjo mund të përfshijë:\", \"Ju mund të siguroheni që njerëzit të dinë për dëshirat tuaja duke folur për to.\", \"Duke shkruar deklaratën tuaj të parapërë, ju mund të ndihmoni për t\'i bërë gjërat të qarta për familjen tuaj, kujdestarët dhe këdo që është i përfshirë në kujdesin tuaj.\", \"Ju mund ta shkruani atë me mbështetjen e të afërmve, kujdestarëve, ose profesionistëve të shëndetit dhe kujdesit social.\"]}, {\"title\": \"A është një deklaratë e parapërë e njëjtë me një vendim të parapërë?\", \"paragraphs\": [\"Jo. Një vendim i parapërë (i njohur gjithashtu si testament i gjallë, ose vendim i parapërë për të refuzuar trajtimin) është një vendim që mund të merrni tani për të refuzuar trajtime specifike në të ardhmen.\", \"Një vendim i parapërë është ligjërisht i detyrueshëm, për sa kohë që plotëson kritere të caktuara.\", \"Lexoni më shumë rreth vendimeve të parapërë për të refuzuar trajtimin\"]}, {\"title\": \"Kush bën një deklaratë të parapërë?\", \"paragraphs\": [\"Ju shkruani një deklaratë të parapërë vetë, për sa kohë që keni kapacitetin mendor për të bërë këto deklarata.\", \"Kapaciteti mendor është aftësia për të marrë vendime. Ndonjëherë, njerëzit nuk kanë kapacitet mendor. Kjo mund të jetë për shumë arsye, duke përfshirë sëmundjen.\", \"Gjeni sesi të bëni, regjistroni ose përfundoni një fuqi ligjore të zgjeruar në GOV.UK, dhe rrethLigjit të Kapacitetit Mendor: marrja e vendimeve në GOV.UK.\"]}, {\"title\": \"A është një deklaratë e parapërë ligjërisht e detyrueshme?\", \"paragraphs\": [\"Jo, një deklaratë e parapërë nuk është ligjërisht e detyrueshme, por kushdo që po merr vendime për kujdesin tuaj duhet ta marrë parasysh atë.\"]}, {\"title\": \"Si ndihmon një deklaratë e parapërë?\", \"paragraphs\": [\"Një deklaratë e parapërë i bën të ditur të gjithëve të përfshirë në kujdesin tuaj për dëshirat, ndjenjat dhe preferencat tuaja nëse nuk jeni në gjendje t\'u tregoni atyre.\"]}, {\"title\": \"A duhet të nënshkruhet dhe të dëshmohet?\", \"paragraphs\": [\"Ju nuk keni pse të nënshkruani një deklaratë të parapërë, por nënshkrimi juaj e bën të qartë se janë dëshirat tuaja që janë shkruar.\"]}, {\"title\": \"Kush duhet ta shohë atë?\", \"paragraphs\": [\"Ju keni fjalën e fundit se kush e sheh atë. Mbajeni atë diku të sigurt, dhe tregoni njerëzve ku është, në rast se ata duhet ta gjejnë atë në të ardhmen.\", \"Ju mund të mbani një kopje në shënimet tuaja mjekësore.\"]}, {\"title\": \"Të mendosh për dëshirat e tua\", \"paragraphs\": [\"Dying Matters ka informacione për të folur për vdekjen. Kjo përfshin ide për të filluar bisedën, për t\'i bërë njerëzit të dinë për dëshirat tuaja, dhe gjëra për të menduar.\"]}, {\"title\": \"Më shumë nëPlanifikimi përpara për kujdesin në fund të jetës\", \"paragraphs\": [\"Faqja e fundit e rishikuar: 19 Shtator 2023Rishikimi i ardhshëm: 19 Shtator 2026\"]}, {\"title\": \"Lidhje mbështetëse\", \"paragraphs\": [\"© E drejta e autorit të Kurorës\"]}], \"sublinks\": null, \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(9,'Pas\n    \n - \n      Aborti Spontan','afterwards_miscarriage','https://www.nhs.uk/conditions/miscarriage/afterwards/','{\"title\": \"Pas\\n    \\n - \\n      Aborti Spontan\", \"url\": \"https://www.nhs.uk/conditions/miscarriage/afterwards/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cooki në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookiet e analizës. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë te shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexonimë shumë rreth cookieve tona para se të zgjidhni.\", \"Mund të ndryshoni cilësimet e cookieve në çdo kohë duke përdorur faqen tonë të cookieve.\", \"Kthehu teAborti Spontan\"]}, {\"title\": \"Përmbajtja\", \"paragraphs\": [\"Një abort spontan mund të ketë një ndikim emocional të thellë tek ju dhe gjithashtu tek partneri juaj, miqtë dhe familja.\", \"Këshilla dhe mbështetje janë në dispozicion gjatë kësaj kohe të vështirë.\"]}, {\"title\": \"Kujtim\", \"paragraphs\": [\"Zakonisht është e mundur të organizohet një memorial dhe shërbim varri nëse dëshironi. Në disa spitale ose klinika mund të jetë e mundur të organizohet një varrosje brenda territorit të institucionit.\", \"Mund të organizoni gjithashtu një varrosje në shtëpi, megjithëse mund të jetë e nevojshme të konsultoheni me autoritetin lokal para se ta bëni këtë.\", \"Kremacioni është një alternativë për varrosjen dhe mund të kryhet ose në spital ose në një kremator lokal. Megjithatë, jo të gjitha krematorët ofrojnë këtë shërbim dhe nuk do të ketë hi për t\'i shpërndarë më pas.\", \"Nëse shtatzënia juaj përfundon para 24 javësh, nuk keni nevojë të regjistroni zyrtarisht një abort spontan. Por mund të mund të merrni një certifikatë në kujtim të foshnjës suaj, nëse dëshironi.\", \"Kërkoni një certifikatë humbje foshnje në GOV.UK\"]}, {\"title\": \"Ndikimi emocional\", \"paragraphs\": [\"Ndonjëherë ndikimi emocional ndihet menjëherë pas abortit spontan, ndërsa në raste të tjera mund të zgjasë disa javë. Shumë njerëz të prekur nga një abort spontan kalojnë një periudhë zie.\", \"Është e zakonshme të ndiheni të lodhur, të humbni oreksin dhe të keni vështirësi për të fjetur pas një aborti spontan. Mund të ndjeni gjithashtu një ndjenjë fajie, tronditje, trishtimi dhe zemërimi – ndonjëherë ndaj një partneri, ose ndaj miqve ose anëtarëve të familjes që kanë pasur shtatzëni të suksesshme.\", \"Njerëzit e ndryshëm zihen në mënyra të ndryshme. Disa njerëz e gjejnë të ngushëllueshme të flasin për ndjenjat e tyre, ndërsa të tjerë e gjejnë temën shumë të dhimbshme për të diskutuar.\", \"Disa njerëz pajtohen me ziejn e tyre pas disa javësh nga aborti spontan dhe fillojnë të planifikojnë për shtatzëninë e tyre të ardhshme. Për të tjerë, mendimi për të planifikuar një shtatzëni tjetër është shumë traumatik, të paktën në afat të shkurtër.\", \"Nëse jeni në një marrëdhënie, mund të ndihmojë të siguroheni që të jeni të dy të hapur për mënyrën se si ndiheni.\", \"Partneri juaj mund të jetë gjithashtu i prekur nga humbja.\", \"Burrat ndonjëherë e gjejnë më të vështirë të shprehin ndjenjat e tyre, veçanërisht nëse ndjejnë se roli i tyre kryesor është të mbështesin nënën dhe jo anasjelltas.\", \"Aborti spontan mund të shkaktojë gjithashtu ndjenja ankthi ose depresioni, dhe mund të çojë në probleme në marrëdhënie.\"]}, {\"title\": \"Marrja e mbështetjes\", \"paragraphs\": [\"Nëse jeni të shqetësuar që ju ose partneri juaj keni probleme për të përballuar ziejn, mund të keni nevojë për trajtim dhe këshillim të mëtejshëm. Ka grupe mbështetjeje që mund të ofrojnë ose të organizojnë këshillim për njerëzit që janë prekur nga aborti spontan.\", \"Lexoni më shumë rreth përballjes me ziejn dhe këshillimit, dhe gjeni shërbime mbështetjeje zieje në zonën tuaj.\", \"Mjeku juaj i përgjithshëm mund t\'ju ofrojë mbështetje dhe këshilla. Organizatat e mëposhtme mund të ndihmojnë gjithashtu:\"]}, {\"title\": \"Kur mund të kem marrëdhënie seksuale përsëri pas një aborti spontan?\", \"paragraphs\": [\"Duhet të shmangni marrëdhëniet seksuale derisa të gjitha simptomat e abortit spontan të kenë zhdukur. Ciklet tuaja menstruale duhet të kthehen brenda 4 deri në 8 javë pas abortit spontan, megjithëse mund të duhen disa muaj për të vendosur një cikël të rregullt.\", \"Nëse nuk dëshironi të bëheni shtatzënë, duhet të përdorni kontracepsion menjëherë. Nëse dëshironi të bëheni shtatzënë përsëri, mund të dëshironi ta diskutoni atë me mjekun tuaj të përgjithshëm ose ekipin kujdesor në spital. Sigurohuni që të ndiheni mirë fizikisht dhe emocionalisht para se të provoni për një shtatzëni tjetër.\", \"Shoqata e Abortit Spontan ka informacione rreth mendimit për një shtatzëni tjetër që mund t\'ju jenë të dobishme. Është e rëndësishme të mbani mend se shumica e aborteve spontane janë një herë dhe pasohen nga një shtatzëni e shëndetshme.\", \"Edhe pse zakonisht nuk është e mundur të parandalohet një abort spontan, ka disa mënyra që mund të zvogëloni rrezikun. Shihni parandalimin e abortit spontan për më shumë informacion dhe këshilla.\"]}, {\"title\": \"Gjetja e një shkaku\", \"paragraphs\": [\"Është e natyrshme të dëshironi të dini pse ndodhi një abort spontan, por fatkeqësisht kjo nuk është gjithmonë e mundur. Shumë aborte spontane mendohen se janë shkaktuar nga një problem një herë me zhvillimin e fetusit.\", \"Lexoni më shumë rreth shkaqeve të abortit spontan.\", \"Faqja e fundit e rishikuar: 09 Mars 2022Rishikimi i ardhshëm: 09 Mars 2025\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit të Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/miscarriage/prevention/\", \"https://www.nhs.uk/conditions/miscarriage/prevention/\", \"https://www.nhs.uk/conditions/miscarriage/prevention/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(10,'Pas një incidenti\n    \n - \n      Ndihmë e parë','after_an_incident_first_aid','https://www.nhs.uk/conditions/first-aid/after-an-accident/','{\"title\": \"Pas një incidenti\\n    \\n - \\n      Ndihmë e parë\", \"url\": \"https://www.nhs.uk/conditions/first-aid/after-an-accident/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund tëlexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorurfaqen tonë të cookies.\", \"Kthehu teNdihmë e parë\"]}, {\"title\": \"Përmbajtja\", \"paragraphs\": [\"Nëse dikush është i lënduar në një incident, së pari kontrolloni që ju dhe personi i lënduar të mos jeni në asnjë rrezik. Nëse jeni, bëni situatën të sigurt.\", \"Kur të jetë e sigurt, vlerësoni personin e lënduar dhe, nëse është e nevojshme, thirrni 999 për një ambulancë. Pastaj mund të kryeni ndihmën e parë bazë.\"]}, {\"title\": \"Vlerësimi i një personi të lënduar\", \"paragraphs\": [\"3 prioritetet kur merreni me një të lënduar zakonisht referohen si ABC, që qëndron për:\"]}, {\"title\": \"Rrugët e frymëmarrjes\", \"paragraphs\": [\"Nëse personi duket i pavetëdijshëm, pyesni me zë të lartë nëse janë në rregull dhe nëse mund të hapin sytë.\", \"Nëse përgjigjen, mund t\'i lini në pozicionin në të cilin janë derisa të arrijë ndihma.\", \"Ndërsa prisni, vazhdoni të kontrolloni frymëmarrjen, pulsionin dhe nivelin e përgjigjes:\", \"Nëse nuk ka përgjigje, lini personin në pozicionin në të cilin është dhe hapni rrugët e frymëmarrjes.\", \"Nëse kjo nuk është e mundur në pozicionin në të cilin janë, vendoseni butësisht mbi shpinë dhe pastaj përpiquni të hapni rrugët e frymëmarrjes.\", \"Për të hapur rrugët e frymëmarrjes, vendosni 1 dorë në ballën e personit dhe anoni kokën butësisht prapa, duke ngritur majën e mjekrës duke përdorur 2 gishta. Kjo e lëviz gjuhën larg nga pjesa e pasme e fytit.\", \"Mos shtypni në dyshemenë e gojës, pasi kjo do të shtyjë gjuhën lart dhe do të bllokojë rrugët e frymëmarrjes.\", \"Nëse mendoni që personi mund të ketë një lëndim në shtyllën kurrizore, vendosni duart tuaja në të dy anët e kokës së tyre dhe përdorni majat e gishtërinjve për të ngritur butësisht këndin e nofullës përpara dhe lart, pa lëvizur kokën, për të hapur rrugët e frymëmarrjes.\", \"Kini kujdes të mos lëvizni qafën e personit. Por hapja e rrugëve të frymëmarrjes ka prioritet mbi një lëndim në qafë. Kjo njihet si teknika e shtytjes së nofullës.\"]}, {\"title\": \"Frymëmarrja\", \"paragraphs\": [\"Për të kontrolluar nëse një person është ende duke frymëmarrë:\", \"Nëse po frymëmarrin normalisht, vendosini nëpozicionin e rikuperimit në mënyrë që rrugët e frymëmarrjes të mbeten të lira nga pengesat, dhe vazhdoni të monitoroni frymëmarrjen normale.\", \"Gërhitja ose frymëmarrja e parregullt nuk është frymëmarrje normale.\", \"Nëse personi nuk po frymëmarrë ose nuk po frymëmarrë normalisht, thirrni 999 për një ambulancë dhe pastaj filloniCPR.\"]}, {\"title\": \"Qarkullimi\", \"paragraphs\": [\"Nëse personi nuk po frymëmarrë normalisht, duhet të filloni kompresionet e gjoksit menjëherë.\", \"Frymëmarrja agonale është e zakonshme në minutat e para pas një arresti të papritur kardiak (kur zemra ndalon të rrahë).\", \"Frymëmarrja agonale është gërhitje e papritur dhe e parregullt. Kjo nuk duhet të ngatërrohet me frymëmarrjen normale dhe CPR duhet të jepet menjëherë.\", \"Nëse personi nuk po frymëmarrë, thirrni 999 për një ambulancë dhe pastaj filloniCPR.\"]}, {\"title\": \"Kurse të ndihmës së parë\", \"paragraphs\": [\"Këshillat e dhëna në këtë faqe mbulojnë vetëm situatat e zakonshme të ndihmës së parë dhe nuk janë një zëvendësim për marrjen e një kursi trajnimi të ndihmës së parë.\", \"Kurset bazë të ndihmës së parë zhvillohen rregullisht në shumicën e zonave në të gjithë Mbretërinë e Bashkuar.\", \"Organizatat që ofrojnë kurse përfshijnë:\", \"Faqja e fundit e rishikuar: 15 Mars 2022Rishikimi i ardhshëm: 15 Mars 2025\"]}, {\"title\": \"Lidhje mbështetëse\", \"paragraphs\": [\"© E drejta e autorit e Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/first-aid/cpr/\", \"https://www.nhs.uk/conditions/first-aid/cpr/\", \"https://www.nhs.uk/conditions/first-aid/cpr/\", \"https://www.nhs.uk/conditions/first-aid/cpr/\"], \"sublink_articles\": [{\"title\": \"CPR\\n    \\n - \\n      Ndihmë e parë\", \"url\": \"https://www.nhs.uk/conditions/first-aid/cpr/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund tëlexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorurfaqen tonë të cookies.\", \"Kthehu teNdihmë e parë\"]}, {\"title\": \"Përmbajtja\", \"paragraphs\": [\"Kjo faqe jep informacion dhe udhëzime rreth rianimacionit kardiopulmonar (CPR). Kjo përfshin CPR vetëm me duar dhe CPR me frymëmarrje shpëtimi.\", \"Nëse dikush është i pavetëdijshëm dhe nuk po frymëmarrë normalisht, thirrni 999 dhe filloni CPR menjëherë.\", \"Kur thirrni 999 për një ambulancë, duhet t\'ju jepen udhëzime bazë për shpëtimin e jetës nëpërmjet telefonit, duke përfshirë këshilla rreth CPR.\"]}, {\"title\": \"CPR tek të rriturit\", \"paragraphs\": [\"Nëse jeni trajnuar në CPR, duke përfshirë frymëmarrje shpëtimi, dhe ndiheni të sigurt duke përdorur aftësitë tuaja, duhet të jepni kompresione të gjoksit me frymëmarrje shpëtimi.\", \"Nëse nuk jeni plotësisht të sigurt, provoni CPR vetëm me duar.\"]}, {\"title\": \"CPR vetëm me duar\", \"paragraphs\": [\"Për të kryer një kompresion të gjoksit:\"]}, {\"title\": \"CPR tek fëmijët\", \"paragraphs\": [\"Duhet të kryeni CPR me frymëmarrje shpëtimi në një fëmijë. Ka më shumë gjasa që fëmijët të kenë një problem me rrugët e frymëmarrjes dhe frymëmarrjen sesa një problem me zemrën.\"]}, {\"title\": \"Foshnjat nën 1 vit\", \"paragraphs\": [\"Faqja e fundit e rishikuar: 15 Mars 2022Rishikimi i ardhshëm: 15 Mars 2025\"]}, {\"title\": \"Lidhje mbështetëse\", \"paragraphs\": [\"© E drejta e autorit e Kurorës\"]}], \"sublinks\": null, \"sublink_articles\": []}]}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(11,'Abuzimi me steroide anabolike','anabolic_steroid_misuse','https://www.nhs.uk/conditions/anabolic-steroid-misuse/','{\"title\": \"Abuzimi me steroide anabolike\", \"url\": \"https://www.nhs.uk/conditions/anabolic-steroid-misuse/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu te Shëndeti A deri në Z\", \"Steroidet anabolike janë ilaçe që mund të merren vetëm me recetë dhe ndonjëherë merren pa këshillën mjekësore për të rritur masën muskulore dhe për të përmirësuar performancën atletike.\", \"Nëse përdoren në këtë mënyrë, ato mund të shkaktojnë efekte anësore serioze dhe varësi.\", \"Steroidet anabolike janë ilaçe të prodhuara që kopjojnë efektet e hormonit mashkullor testosteron. Ato kanë përdorime të kufizuara mjekësore dhe nuk janë të njëjta me kortikosteroidet, një lloj tjetër i ilaçeve steroidale që janë më të përshkruara zakonisht.\"]}, {\"title\": \"A janë steroidet anabolike të paligjshme?\", \"paragraphs\": [\"Steroidet anabolike janë drogë të klasës C, të cilat mund të lëshohen vetëm nga farmacistët me një recetë.\", \"Është ligjore të kesh steroide anabolike për përdorim personal. Ato gjithashtu mund të importohen ose eksportohen, për sa kohë që kjo kryhet personalisht. Kjo do të thotë që ato nuk mund të dërgohen me postë ose të dorëzohen nga një shërbim kuriere ose transporti.\", \"Megjithatë, është e paligjshme të posedosh, importosh ose eksportosh steroide anabolike nëse besohet se po i furnizon ose shet. Kjo përfshin edhe dhënien e tyre për miq. Ndëshkimi është një gjobë e pakufizuar, ose madje një dënim burgu deri në 14 vjet.\", \"Në sportin profesional, shumica e organizatave ndalojnë përdorimin e steroideve anabolike dhe testojnë konkurrentët për steroide të ndaluara. Nëse testoni pozitiv, mund të jeni të ndaluar nga konkurrimi profesional.\"]}, {\"title\": \"Pse njerëzit abuzojnë me steroide anabolike\", \"paragraphs\": [\"Steroidet anabolike mund të përdoren si ilaçe që rrisin performancën, duke rritur masën muskulore dhe duke ulur yndyrën, si dhe duke shkaktuar shumë efekte të padëshirueshme. Disa njerëz i marrin rregullisht për të përmirësuar performancën fizike dhe për të ndërtuar trupin e tyre.\", \"Megjithatë, njerëz të të gjitha moshave janë të njohur që abuzojnë me këto ilaçe, përfshirë djemtë adoleshentë që vuajnë nga çrregullimi i trupit. Kjo është një gjendje shëndetësore mendore ku një person kalon shumë kohë duke u shqetësuar për të metat në pamjen e tyre. Këto të meta shpesh janë të padukshme për të tjerët.\", \"Njerëzit që kanë çrregullim të trupit mund të marrin steroide anabolike sepse nuk e shohin veten si mjaftueshëm të mëdhenj ose të fortë fizikisht.\", \"Disa njerëz besojnë se marrja e steroideve anabolike do t\'i ndihmojë të bëhen të fit dhe të shëndetshëm. Kjo nuk është e vërtetë: marrja e steroideve anabolike është një zakon i rrezikshëm drogash.\"]}, {\"title\": \"Si merren steroide anabolike\", \"paragraphs\": [\"Steroidet anabolike zakonisht injektohen në një muskull ose merren me gojë si tableta, por ato gjithashtu vijnë si krem ose xhele që aplikohen në lëkurë.\", \"Shumë njerëz që përdorin steroide anabolike janë të vetëdijshëm për rreziqet e marrjes së tyre dhe besojnë se duke marrë ilaçet në mënyra të caktuara mund të shmangin efektet anësore. Ose mund të marrin ilaçe shtesë për të përpiqur të kundërshtojnë efektet anësore.\", \"Përdoruesit mund:\", \"Por nuk ka prova që ndonjë nga këto metoda zvogëlon efektet anësore dhe dëmet nga marrja e steroideve anabolike.\", \"Përdoruesit priren të ushtrohen më shumë kur marrin doza të larta për të përfituar sa më shumë nga performanca e përmirësuar gjatë kësaj kohe.\"]}, {\"title\": \"Efektet anësore të steroideve anabolike\", \"paragraphs\": [\"Marrja e rregullt e steroideve anabolike mund të çojë në ndryshime fizike dhe psikologjike si tek burrat ashtu edhe tek gratë, si dhe në gjendje mjekësore potencialisht të rrezikshme.\"]}, {\"title\": \"Efektet fizike\", \"paragraphs\": [\"Efektet e steroideve anabolike tek burrat mund të përfshijnë:\", \"Tek gratë, steroide anabolike mund të shkaktojnë:\", \"Përveç kësaj, si burrat ashtu edhe gratë që marrin steroide anabolike mund të zhvillojnë ndonjë nga gjendjet mjekësore të mëposhtme:\"]}, {\"title\": \"Efektet psikologjike\", \"paragraphs\": [\"Abuzimi me steroide anabolike mund të shkaktojë edhe efektet psikologjike ose emocionale të mëposhtme:\"]}, {\"title\": \"Rritje e ndaluar tek adoleshentët\", \"paragraphs\": [\"Steroidet anabolike përshpejtojnë rritjen e kockave, kështu që nëse abuzohen nga adoleshentët që ende nuk kanë pasur rritjen e shpejtë të lidhur me pubertetin, ilaçet mund të shkaktojnë plakje të parakohshme të kockave dhe rritje të kufizuar.\"]}, {\"title\": \"Ndarja e gjilpërave\", \"paragraphs\": [\"Meqenëse steroide anabolike shpesh injektohen, ka rreziqe të lidhura me ndarjen e gjilpërave. Këto janë të njëjtat rreziqe të lidhura me përdorimin e drogave rekreative dhe përfshijnë:\"]}, {\"title\": \"Varësia\", \"paragraphs\": [\"Steroidet anabolike janë adiktive. Kjo do të thotë që mund të keni një dëshirë të fortë për ilaçin, të keni nevojë për më shumë për të marrë të njëjtin efekt dhe të keni simptoma tërheqjeje nëse ndaloni papritur marrjen e tij.\", \"Një person që është i varur nga steroide anabolike do të dëshirojë të vazhdojë t\'i përdorë pavarësisht nga përjetimi i efekteve anësore fizike të pakëndshme.\", \"Kur mjekët përshkruajnë ilaçe steroidale, ata gjithmonë këshillojnë të ndaloni ilaçet ngadalë duke ulur gradualisht dozën.\", \"Ndërprerja e papritur e steroideve anabolike mund të rezultojë në simptoma tërheqjeje që përfshijnë:\"]}, {\"title\": \"Marrja e ndihmës\", \"paragraphs\": [\"Ju duhet të konsultoni një mjek të përgjithshëm nëse mendoni se jeni të varur nga steroide anabolike. Trajtimi për një varësi nga steroide anabolike do të jetë i ngjashëm me atë të llojeve të tjera të varësisë.\", \"Mjeku i përgjithshëm mund t\'ju referojë një këshilltar të trajnuar posaçërisht për drogat. Ata do të diskutojnë varësinë tuaj me ju, si të ndaloni sigurisht marrjen e steroideve dhe çdo pengesë që mund të hasni kur përpiqeni të ndaloni, plus këshilla për të përballuar këto pengesa.\", \"Informacione dhe këshilla të mëtejshme:\", \"Faqja e fundit e rishikuar: 13 Prill 2022Rishikimi i ardhshëm: 13 Prill 2025\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit të Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/body-dysmorphia/\", \"https://www.nhs.uk/conditions/infertility/\", \"https://www.nhs.uk/conditions/erection-problems-erectile-dysfunction/\", \"https://www.nhs.uk/conditions/hair-loss/\", \"https://www.nhs.uk/conditions/prostate-cancer/\", \"https://www.nhs.uk/conditions/acne/\", \"https://www.nhs.uk/conditions/periods/\", \"https://www.nhs.uk/conditions/hair-loss/\", \"https://www.nhs.uk/conditions/acne/\", \"https://www.nhs.uk/conditions/heart-attack/\", \"https://www.nhs.uk/conditions/high-cholesterol/\", \"https://www.nhs.uk/conditions/hallucinations/\", \"https://www.nhs.uk/conditions/gangrene/\", \"https://www.nhs.uk/conditions/hepatitis-b/\", \"https://www.nhs.uk/conditions/hepatitis-c/\", \"https://www.nhs.uk/conditions/hiv-and-aids/\", \"https://www.nhs.uk/conditions/clinical-depression/\", \"https://www.nhs.uk/conditions/insomnia/\", \"https://www.nhs.uk/conditions/anorexia/\", \"https://www.nhs.uk/conditions/headaches/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(12,'Anafilaksia','anaphylaxis','https://www.nhs.uk/conditions/anaphylaxis/','{\"title\": \"Anafilaksia\", \"url\": \"https://www.nhs.uk/conditions/anaphylaxis/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Ne kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Gjithashtu do të donim të përdornim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbime të quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne i përdorim këto informacione për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Ne do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tek Shëndeti A deri në Z\", \"Anafilaksia është një reaksion alergjik jetëkërcënues që ndodh shumë shpejt. Mund të shkaktohet nga ushqimi, ilaçet ose kafshimet e insekteve. Thirrni 999 nëse mendoni se ju ose dikush tjetër po përjeton një reaksion anafilaktik.\"]}, {\"title\": \"Kontrolloni nëse është anafilaksi\", \"paragraphs\": [\"Simptomat e anafilaksisë ndodhin shumë shpejt.\", \"Zakonisht fillojnë brenda pak minutash pas kontaktimit me diçka për të cilën jeni alergjik, si p.sh. ushqim, ilaç ose kafshim insekti.\", \"Simptomat përfshijnë:\", \"Mund të keni edhe një vyrë të ënjtur, të ngritur ose që kruhet.\"]}, {\"title\": \"Veprim i menjëhershëm i kërkuar: Thirrni 999 nëse:\", \"paragraphs\": [\"Ju ose personi që është i sëmurë mund të keni edhe një vyrë të ënjtur, të ngritur ose që kruhet.\", \"Këto mund të jenë shenja të një reaksioni alergjik serioz dhe mund të kenë nevojë për trajtim të menjëhershëm në spital.\"]}, {\"title\": \"Çfarë të bëni nëse keni anafilaksi\", \"paragraphs\": [\"Ndiqni këto hapa nëse mendoni se ju ose dikush që është me ju po përjeton një reaksion anafilaktik:\", \"Mos qëndroni në këmbë ose mos ecni në asnjë kohë, edhe nëse ndiheni më mirë.\"]}, {\"title\": \"Si të përdorni një auto-injektor adrenalini\", \"paragraphs\": [\"Ka lloje të ndryshme të auto-injektorëve të adrenalinit dhe secili jepet ndryshe.\"]}, {\"title\": \"Trajtimi për anafilaksinë\", \"paragraphs\": [\"Anafilaksia duhet të trajtohet në spital menjëherë.\", \"Trajtimet mund të përfshijnë:\", \"Zakonisht do të qëndroni në spital për rreth 2 deri 12 orë, por mund të keni nevojë të qëndroni më gjatë.\", \"Para se të largoheni nga spitali, do t\'ju jepen 2 auto-injektorë adrenalini për t\'i mbajtur në rast se keni një reaksion tjetër anafilaktik.\", \"Një auto-injektor adrenalini është një pajisje e veçantë për të injektuar adrenalinin vetë. Do t\'ju tregohet si dhe kur ta përdorni atë.\", \"Duhet t\'ju tregohet si të përdorni auto-injektorin tuaj të adrenalinit çdo herë që ju përshkruhet.\", \"Mund të referoheni gjithashtu te një specialist alergjish për teste dhe këshilla.\"]}, {\"title\": \"Gjërat që mund të bëni për të ndihmuar në parandalimin e anafilaksisë\", \"paragraphs\": [\"Ka disa gjëra që mund të bëni për të ndihmuar në parandalimin e anafilaksisë ose për t\'u përgatitur nëse ndodh.\"]}, {\"title\": \"Bëni\", \"paragraphs\": [\"shmangni ushqimin, ilaçin ose gjënë për të cilën jeni alergjik – për shembull, nëse keni një alergji ushqimore, kontrolloni me kujdes etiketat e ushqimeve dhe informoni stafin në restorante dhe kafe për alergjinë tuaj\", \"mbajini gjithmonë me vete 2 auto-injektorë adrenalini\", \"kontrolloni rregullisht datat e skadencës së auto-injektorëve tuaj të adrenalinit dhe merrni të rinj para se të skadojnë\", \"praktikoni si të përdorni auto-injektorin tuaj të adrenalinit duke përdorur një injektor trajnues (një injektor që nuk ka gjilpërë ose ilaç) – mund ta porositni online nga kompania që prodhon injektorin tuaj\", \"mësoni miqtë, familjen, kolegët ose kujdestarët si dhe kur të përdorin auto-injektorin tuaj të adrenalinit\", \"përdorni auto-injektorin tuaj të adrenalinit nëse mendoni se mund të keni anafilaksi, edhe nëse simptomat tuaja janë të lehta\", \"vishni xhevahirë mjekësore si p.sh. një byzylyk me informacione rreth alergjisë tuaj – kjo i tregon të tjerëve për alergjinë tuaj në rast emergjence\"]}, {\"title\": \"Mos bëni\", \"paragraphs\": [\"mos i lini auto-injektorët tuaj të adrenalinit në vende shumë të nxehta ose të ftohta si p.sh. në frigorifer ose jashtë në diell\"]}, {\"title\": \"Më shumë informacion\", \"paragraphs\": [\"Ka më shumë këshilla se çfarë mund të bëni për të shmangur alergjitë e zakonshme. Mësoni më shumë rreth:\"]}, {\"title\": \"Shkaqet e anafilaksisë\", \"paragraphs\": [\"Anafilaksia ndodh kur trupi juaj ka një reaksion serioz ndaj diçkaje për të cilën jeni alergjik.\", \"Alergjitë që ndonjëherë mund të shkaktojnë anafilaksi përfshijnë:\", \"Ndonjëherë nuk dihet se çfarë ka shkaktuar një reaksion anafilaktik.\", \"Faqja e fundit e rishikuar: 21 Qershor 2023Rishikimi i ardhshëm: 21 Qershor 2026\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e mbretërisë\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/food-allergy/\", \"https://www.nhs.uk/conditions/insect-bites-and-stings/\", \"https://www.nhs.uk/conditions/nsaids/\", \"https://www.nhs.uk/conditions/anaesthesia/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41'),(13,'Kontrollet vjetore të shëndetit\n    \n - \n      Aftësi të kufizuara të të mësuarit','annual_health_checks_learning_disabilities','https://www.nhs.uk/conditions/learning-disabilities/annual-health-checks/','{\"title\": \"Kontrollet vjetore të shëndetit\\n    \\n - \\n      Aftësi të kufizuara të të mësuarit\", \"url\": \"https://www.nhs.uk/conditions/learning-disabilities/annual-health-checks/\", \"sections\": [{\"title\": \"Cookies në faqen e NHS\", \"paragraphs\": [\"Kemi vendosur disa skedarë të vegjël të quajtur cookies në pajisjen tuaj për të bërë që faqja jonë të funksionojë.\", \"Do të donim gjithashtu të përdorim cookies analitike. Këto mbledhin feedback dhe dërgojnë informacione se si përdoret faqja jonë tek shërbimet e quajtura Adobe Analytics, Adobe Target, Qualtrics Feedback dhe Google Analytics. Ne përdorim këtë informacion për të përmirësuar faqen tonë.\", \"Na tregoni nëse kjo është në rregull. Do të përdorim një cookie për të ruajtur zgjedhjen tuaj. Mund të lexoni më shumë rreth cookies tona para se të zgjidhni.\", \"Ju mund të ndryshoni cilësimet e cookies në çdo kohë duke përdorur faqen tonë të cookies.\", \"Kthehu tek Aftësi të kufizuara të të mësuarit\"]}, {\"title\": \"Përmbajtja\", \"paragraphs\": [\"Njerëzit me aftësi të kufizuara të të mësuarit shpesh kanë shëndet fizik dhe mendor më të dobët se njerëzit e tjerë. Kjo nuk duhet të jetë kështu.\", \"Është e rëndësishme që çdo person mbi 14 vjeç që është në regjistrin e aftësive të kufizuara të të mësuarit të mjekut të ketë një kontroll vjetor të shëndetit.\", \"Një kontroll vjetor i shëndetit mund t\'ju ndihmojë të qëndroni mirë duke folur me një mjek ose infermiere për shëndetin tuaj dhe duke gjetur çdo problem herët, në mënyrë që ato të mund të zgjidhen.\", \"Nuk keni nevojë të jeni i sëmurë për të bërë një kontroll shëndetësor – në fakt, shumica e njerëzve bëjnë kontrollin e tyre vjetor të shëndetit kur ndihen mirë.\", \"Nëse jeni të shqetësuar për të parë një mjek, ose ka diçka që ata mund të bëjnë për ta bërë takimin tuaj më të mirë, njoftoni mjekun ose infermieren.\", \"Ata mund të bëjnë ndryshime për t\'ju ndihmuar. Këto quhen rregullime të arsyeshme.\", \"Mësoni më shumë rreth kontrollit vjetor të shëndetit në këtë broshurë të lehtë për të lexuar nga Mencap (PDF 2.72Mb)\"]}, {\"title\": \"Kush mund të ketë një kontroll vjetor të shëndetit?\", \"paragraphs\": [\"Çdo person i moshës 14 vjeç e lart që është në regjistrin e aftësive të kufizuara të të mësuarit të mjekut mund të ketë një kontroll vjetor të shëndetit falas një herë në vit.\", \"Ju mund të kërkoni të regjistroheni në këtë regjistër nëse mendoni se keni një aftësi të kufizuar të të mësuarit.\", \"Kontrolloni me praktikën e mjekut tuaj nëse ju ose personi për të cilin kujdeseni jeni në regjistër.\"]}, {\"title\": \"Si do të ndihmojë një kontroll vjetor i shëndetit?\", \"paragraphs\": [\"Ju do të njiheni më mirë me mjekun tuaj. Mjeku gjithashtu do të jetë në gjendje të zbulojë çdo problem shëndetësor më shpejt në mënyrë që të merrni trajtimin që ju nevojitet për të qëndruar mirë.\", \"Ju mund t\'i bëni pyetje mjekut tuaj për shëndetin tuaj dhe t\'i tregoni atij se si ndiheni.\", \"Ju mund gjithashtu të flisni për çdo trajtim që po merrni ose ilaç që përdorni.\"]}, {\"title\": \"Si mund të merrni një takim?\", \"paragraphs\": [\"Të rriturit dhe të rinjtë e moshës 14 vjeç e lart me aftësi të kufizuara të të mësuarit që janë në regjistrin e aftësive të kufizuara të të mësuarit të praktikës së mjekut duhet të ftohen nga mjeku i tyre për të ardhur për një kontroll vjetor të shëndetit.\", \"Shumica e mjekëve ofrojnë kontroll vjetor të shëndetit për njerëzit me aftësi të kufizuara të të mësuarit. Por ata nuk janë të detyruar të ofrojnë këtë shërbim.\", \"Nëse praktika e mjekut tuaj nuk ju ka ofruar një kontroll vjetor të shëndetit, ju mund t\'i kërkoni atyre nëse mund të keni një të tillë.\", \"Nëse ata thonë jo, kërkoni ndihmë nga ekipi lokal i aftësive të kufizuara të të mësuarit në komunitet për të marrë një të tillë.\"]}, {\"title\": \"Video: Kontrollet vjetore të shëndetit për njerëzit me aftësi të kufizuara të të mësuarit\", \"paragraphs\": [\"Kjo video shpjegon çfarë ndodh gjatë një kontrolli vjetor të shëndetit.\", \"Kjo është një dritare modale.\", \"Fillimi i dritares së dialogut. Escape do të anulojë dhe do ta mbyllë dritaren.\", \"Fundi i dritares së dialogut.\", \"Kjo është një dritare modale. Kjo modale mund të mbyllet duke shtypur tastin Escape ose duke aktivizuar butonin e mbylljes.\", \"Kjo është një dritare modale. Kjo modale mund të mbyllet duke shtypur tastin Escape ose duke aktivizuar butonin e mbylljes.\"]}, {\"title\": \"Çfarë ndodh gjatë kontrollit vjetor të shëndetit?\", \"paragraphs\": [\"Ju mund të shihni profesionistë të ndryshëm të shëndetit. Këta mund të përfshijnë një mjek, një farmacist, një infermiere ose një asistent shëndetësor. Të gjithë kanë marrë trajnim shtesë për të qenë në gjendje të kryejnë kontrollin e shëndetit.\", \"Gjatë kontrollit të shëndetit, profesionisti i shëndetit do të:\", \"Ndonjëherë njerëzit me aftësi të kufizuara të të mësuarit ose autizëm u jepen ilaçe që mund të mos kenë nevojë.\", \"Mësoni më shumë rreth STOMP (Ndërprerja e mbidozimit të ilaçeve për njerëzit me aftësi të kufizuara të të mësuarit, autizëm ose të dyja) në këtë broshurë të lehtë për të lexuar nga NHS (PDF 480kb)\", \"Ju do të pyeteni nëse jeni në rregull (jepni pëlqimin tuaj) për të ndarë informacionin tuaj të shëndetit me shërbime të tjera shëndetësore për t\'u siguruar që merrni mbështetjen e duhur nëse shkoni në spital, për shembull.\", \"Prindërit tuaj ose kujdestari juaj kryesor mund ta bëjnë këtë për ju nëse nuk jeni në gjendje.\", \"Profesionisti i shëndetit mund t\'ju japë informacion shëndetësor, si këshilla për të ngrënë shëndetshëm, ushtrime fizike, kontracepsion ose ndërprerje të duhanpirjes.\"]}, {\"title\": \"Bërja e rregullimeve të arsyeshme për ju\", \"paragraphs\": [\"NHS duhet ta bëjë aq të lehtë për njerëzit me aftësi të kufizuara të përdorin shërbimet shëndetësore sa është për njerëzit që nuk janë me aftësi të kufizuara. Kjo quhet bërja e rregullimeve të arsyeshme.\", \"Pyetni mjekun tuaj nëse keni nevojë për ndonjë rregullim të arsyeshëm, si:\", \"Rregullimet e arsyeshme që ju nevojiten duhet të shkruhen në një profil shëndetësor ose plan veprimi shëndetësor që mjeku ose infermierja mund të përdorin.\", \"Shikoni një video rreth rregullimeve të arsyeshme dhe si ato mund t\'ju ndihmojnë në YouTube\"]}, {\"title\": \"A duhet të keni një kontroll vjetor të shëndetit?\", \"paragraphs\": [\"Jo. Ju mund të zgjidhni nëse dëshironi të keni një kontroll vjetor të shëndetit apo jo.\", \"Ju mund t\'i kërkoni mjekut ose infermieres më shumë informacione rreth kontrollit vjetor të shëndetit para se të vendosni.\", \"Ju do të pyeteni nëse jeni në rregull (jepni pëlqimin tuaj) për të kryer ndonjë test ose procedurë para se t\'i bëni ato.\"]}, {\"title\": \"A është e njëjtë me Kontrollin e Shëndetit të NHS?\", \"paragraphs\": [\"Jo. Programi i Kontrollit të Shëndetit të NHS është për të gjithë të rriturit e moshës 40 deri në 74 vjeç.\", \"Ai vlerëson rrezikun e tyre për sëmundje të zemrës, stroke, sëmundje të veshkave, diabet dhe demencë çdo 5 vjet.\", \"Mësoni më shumë rreth Kontrollit të Shëndetit të NHS\", \"Faqja e fundit e rishikuar: 10 Janar 2022Rishikimi i ardhshëm: 10 Janar 2025\"]}, {\"title\": \"Lidhjet e mbështetjes\", \"paragraphs\": [\"© E drejta e autorit e Kurorës\"]}], \"sublinks\": [\"https://www.nhs.uk/conditions/learning-disabilities/diagnosis/\", \"https://www.nhs.uk/conditions/learning-disabilities/going-into-hospital/\", \"https://www.nhs.uk/conditions/epilepsy/\", \"https://www.nhs.uk/conditions/constipation/\", \"https://www.nhs.uk/conditions/swallowing-problems-dysphagia/\", \"https://www.nhs.uk/conditions/asthma/\", \"https://www.nhs.uk/conditions/diabetes/\", \"https://www.nhs.uk/conditions/consent-to-treatment/\", \"https://www.nhs.uk/conditions/coronary-heart-disease/\", \"https://www.nhs.uk/conditions/stroke/\", \"https://www.nhs.uk/conditions/kidney-disease/\", \"https://www.nhs.uk/conditions/diabetes/\", \"https://www.nhs.uk/conditions/dementia/about-dementia/what-is-dementia/\", \"https://www.nhs.uk/conditions/nhs-health-check/\", \"https://www.nhs.uk/conditions/learning-disabilities/diagnosis/\"], \"sublink_articles\": []}','2025-03-18 19:03:41','2025-03-18 19:03:41');
